import React from 'react'
import EditIcon from '@mui/icons-material/Edit';
import { useNavigate } from 'react-router-dom';

function Favorites() {
    const navigate=useNavigate();
    const editProfileHandler=()=>{
        navigate("/Profile");

    }
  return (
    <div>

      <Navigationbar/>
      <hr></hr>
      <div className="shophome_header">
        <div className="shop_details">

          <img
            className="shop_img"
            src={shopimage}  alt=""
          ></img>
          <div className="shop_info">
            <h3 className="shop_name">{user}</h3>
            <EditIcon onClick={ediProfileHandler}></EditIcon>

          </div>
        </div>
        <div className="owner_details">
          <h6 style={{ fontSize: "18px" }}>SHOP OWNER</h6>
          <img
            style={{ width: "30%", borderRadius: "50%", height: "100px" }}
            //src={require("../uploads/"+shopimage[0].shopImage)}
          ></img>

          <h5>{user.name}</h5>
        </div>
      </div>
      <div className="shop_items" style={{backgroundColor:"rgb(243, 234, 223)"}}>
        <div>
          <button
            style={{
              marginLeft: "7.5%",
              padding: "10px",
              width: "25%",
              backgroundColor: "hsl(18, 85%, 55%)",
              border: "none",
              color: "white",
              borderRadius:"20px"
            }}
            onClick={addItems}
          >
            Add Item
          </button>
        </div>
        <div style={{backgroundColor:"rgb(243, 234, 223)"}}>
          <div style={{ width: "75%", margin: "3rem auto" }}>
            <div style={{ textAlign: "center" }}></div>
            <div
              style={{
                display: "flex",
                justifyContent: "flex-end",
                margin: "1rem auto",
              }}
            ></div>
            {products.length === 0 ? (
              <div
                style={{
                  display: "flex",
                  height: "300px",
                  justifyContent: "center",
                  alignItems: "center",
                }}
              >
                <h2>No post yet...</h2>
              </div>
            ) : (

              <div className="container-fluid mx-1">
                <div className="row mt-5 mx-1">
                  <div className="col-md-15">
                    <div className="row">{renderCards}</div>
                  </div>
                </div>
              </div>

            )}

            <br />
            <br />
            {postSize >= limit && (
              <div style={{ display: "flex", justifyContent: "center" }}>
                <button onClick={onLoadMore}>Load More</button>
              </div>
            )}
          </div>
        </div>
      </div>

      {showProductsAddPage && (
        <Popup setShowProductsAddPage={setShowProductsAddPage} />
      )}

    </div>
  )
}

export default Favorites
