import React, {useEffect, useState} from "react";
import Logo from "../Images/etsylogo.png"
import "../css/Home.css";
import Product from "./Product";

import Navigationbar from "./Navigationbar";
import Axios  from 'axios';
import { Col, Card, Row} from 'antd'
const {Meta}=Card



function Home(){

    const [Products, setProducts]=useState([])
    const [Skip, setSkip] = useState(0)
    const [Limit, setLimit] = useState(8)
    const [PostSize, setPostSize] = useState()
    const [SearchTerms, setSearchTerms] = useState("")


    useEffect(() => {
      const variables ={
        skip:Skip,
        limit:Limit,
      }
      getProducts(variables)
    },[])
    const getProducts=(variables) =>{
      Axios.post('http://localhost:5000/getProducts',variables)
      .then((response) =>{
        if(response.data.success){
          if (variables.loadMore) {
            setProducts([...Products, ...response.data.result])
            console.log(Products);
        } else {
            setProducts(response.data.result)
        }
        setPostSize(response.data.postSize)     
        }else{
          alert("failed to fetch products")
        }
      })
    }

    const renderCards = Products.map((product, index) => {

      return (<Col lg={6} md={8} xs={24}>
             <Card
                style={{
                width: 400,
                backgroundColor: "yellow",
                border:"1px",
              }}
              cover={<a><img style={{width:'200px', maxHeight:'150px'}} src={require("../uploads/"+product.image)} alt=""/></a>}
          >
              <Card.Meta
                  title={product.productname}
                  description={`$${product.price}`}
              />
          </Card>
      </Col>)
  })
  const onLoadMore = () => {
    let skip = Skip + Limit;

    const variables = {
        skip: skip,
        limit: Limit,
        loadMore: true,

        searchTerm: SearchTerms
    }
    getProducts(variables)
    setSkip(skip)
  }
    const updateSearchTerms = (newSearchTerm) => {

      const variables = {
          skip: 0,
          limit: Limit,

          searchTerm: newSearchTerm
      }

      setSkip(0)
      setSearchTerms(newSearchTerm)

      getProducts(variables)
  }

    return (
      <div>
        <Navigationbar/>
      <div style={{ width: '75%', margin: '3rem auto' }}>
            <div style={{ textAlign: 'center' }}>

            </div>
            <div style={{ display: 'flex', justifyContent: 'flex-end', margin: '1rem auto' }}>



            </div>
            {Products.length === 0 ?
                <div style={{ display: 'flex', height: '300px', justifyContent: 'center', alignItems: 'center' }}>
                    <h2>No post yet...</h2>
                </div> :
                <div>
                    <Row className="card-row">

                        {renderCards}

                    </Row>


                </div>
              } 
              <br /><br />
              {PostSize >= Limit &&
                <div style={{ display: 'flex', justifyContent: 'center' }}>
                    <button onClick={onLoadMore}>Load More</button>
                </div>
            }


      </div>
      </div>
    );
}

export default Home
