import React,{useEffect, useState} from 'react'
import Axios from 'axios';
import "../index.css";
import {Link, Navigate, useParams} from 'react-router-dom'
import {useNavigate} from 'react-router'

function EditImage() {
    const [itemImage, setItemImage] = useState("");
    const {id}=useParams();
    const product_id=Number(id);
    const updateImage=()=>{
        Axios.post(`http://localhost:5000/updateImage/${product_id}`,itemImage,{
            headers: { "content-Type": "multipart/form-data" },
          })
        .then(response=>{
            if(response.data.success)
            {
                console.log("Image update successfully");

            }
            else{
                console.log("Image upload failed");
            }
        }).catch(err => { console.log(err);})
    }


  return (
    <div>
        <div className="htmlForm-group">
            <label htmlFor="item_image">Item Image</label>
            <br />
            <input
              style={{ border: "none" }}
              type="file"
              name="itemImage"
              className="item_image"
              id="item_image"
              onChange={(event) => {
                setItemImage(event.target.files[0]);
              }}
              required
            />
            <button type="submit" onClick={updateImage}>confirm</button>
    </div>
    </div>
  )
}

export default EditImage
