import { createSlice } from "@reduxjs/toolkit";

export const cartSlice = createSlice({
  name: "cart",
  initialState: {
    cartProducts: [],
    finalCartProducts: [],
    qtyTab: [],
  },
  reducers: {
    // by id
    cartProducts: (state, action) => {
      state.cartProducts = action.payload;
    },
    removeCartPreviewProducts: (state) => {
      state.cartProducts = null;
    },
    addProductToCart: (state, action) => {
      state.finalCartProducts = action.payload;
    },
    removeCartProduct: (state) => {
      state.finalCartProducts = null;
    },

    addQtyToCart: (state, action) => {
      state.qtyTab.push(action.payload);
    },
  },
});

export const {
  cartProducts,
  addProductToCart,
  removeCartPreviewProducts,
  removeCartProduct,
  addQtyToCart,
} = cartSlice.actions;

export const getAllCartProducts = (state) => state.cart.cartProducts;
export const getFinalCartProducts = (state) => state.cart.finalCartProducts;
export const getQtyFromRedux = (state) => state.cart.qtyTab;

export default cartSlice.reducer;
