import React, { useState, useEffect } from "react";
import profilepicture from "../Images/profilepicture.png"
import EditProduct from "./EditProduct"
import "../App.css"
import Popup from "./Popup";
import Axios from "axios";
import { Col, Card, Row } from "antd";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import userSlice, { selectUser } from "../features/userSlice";
function ShopHome() {

  const user = useSelector(selectUser);

  const [products, setProducts] = useState([]);
  const [showProds, setShowProds] = useState(false);
  const [Skip, setSkip] = useState(0);
  const [limit, setLimit] = useState(8);
  const [showProductsAddPage, setShowProductsAddPage] = useState(false);
  const [editProductPage, setEditProductPage] = useState(false);
  const [postSize, setPostSize] = useState();
  const [shopProfileImage,setShopProfileImage]=useState("")

  const addItems = () => {
    setShowProductsAddPage(true);
  };

  useEffect(() => {
    const variables = {
      skip: Skip,
      limit: limit,
    };
    viewItems(variables);
  }, []);

  const onLoadMore = () => {

    let skip = Skip + limit;
    console.log(skip + " in load more");
    const variables = {
      skip: skip,
      limit: limit,
      loadMore: true,
    };
    viewItems(variables);
    setSkip(skip);
  };

  const viewItems = (variables) => {
    setShowProds(true);
    console.log("---------------in view Items-------------------");
    console.log(variables.skip + " in get all products");
    Axios.post(
      "http://localhost:5000/getAllProducts" ,
      variables
    ).then((response) => {
      console.log(response);
      if (response.data.success) {
        if (variables.loadMore) {
          setProducts([...products, ...response.data.result]);
          console.log(products);
        } else {
          setProducts(response.data.result);
        }
        setPostSize(response.data.postSize);
        console.log(response.data.postSize + "Postsize in getallProducts");
      } else {
        console.log("Failed in ");
      }
    });
  };
  const updateImage=(e)=>{
    e.preventDefault();
    const formData = new FormData();
    formData.append("itemImage", shopProfileImage);
   console.log("image"+shopProfileImage);
    Axios.post(`http://localhost:5000/updateshopImage`,formData,{
        headers: { "content-Type": "multipart/form-data" },
      })
    .then(response=>{
        if(response.data.success)
        {
            console.log("Image update successfully");

        }
        else{
            console.log("Image upload failed");
        }
    }).catch(err => { console.log(err);})
}


  const renderCards = products.map((product) => {
    return (
      <Col lg={6} md={8} xs={24}>
        <Card
          hoverable={true}
          cover={
            <div>
              <p>{product.productname}</p>
              <img style={{width:"200px"}} src={require("../uploads/"+product.image)} alt="" />

              <Link to={`EditProduct/${product.productid}`} className="btn btn-success btn-sm">edit product</Link>
              <Link to={`EditImage/${product.productid}`} className="btn btn-success btn-sm">edit image</Link>
            </div>

          }
        >
          <Card.Meta
            title={product.description}
            description={`$${product.price}`}

          />
        </Card>
      </Col>
    );
  });

  return (
    <div>


      <hr></hr>
      <div className="shophome_header">
        <div className="shop_details">
          <img
            className="shop_img"
            src={shopProfileImage} 
          ></img>
          <div className="shop_info">
            <h3 className="shop_name">SunnySelfCare</h3>
            <p>0 sales </p>
            <div className="htmlForm-group">
            <label htmlFor="item_image">Edit Shop</label>
            <br />
            <input
              style={{ border: "none" }}
              type="file"
              name="itemImage"
              className="item_image"
              id="item_image"
              onChange={(event) => {
                setShopProfileImage(event.target.files[0]);
              }}
              required
            />
            </div>
          </div>
        </div>
        <div className="owner_details">
          <h6 style={{ fontSize: "18px" }}>SHOP OWNER</h6>
          <img
            style={{ width: "30%", borderRadius: "50%", height: "100px" }}
            src="https://images.pexels.com/photos/11376531/pexels-photo-11376531.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260"
          ></img>
          <h5>Ramya</h5>
        </div>
      </div>
      <div className="shop_items">
        <div>
          <button
            style={{
              marginLeft: "7.5%",
              padding: "10px",
              width: "25%",
              backgroundColor: "gray",
              border: "none",
              color: "white",
            }}
            onClick={addItems}
          >
            Add More Items..!
          </button>
        </div>
        <div>
          <div style={{ width: "75%", margin: "3rem auto" }}>
            <div style={{ textAlign: "center" }}></div>
            <div
              style={{
                display: "flex",
                justifyContent: "flex-end",
                margin: "1rem auto",
              }}
            ></div>
            {products.length === 0 ? (
              <div
                style={{
                  display: "flex",
                  height: "300px",
                  justifyContent: "center",
                  alignItems: "center",
                }}
              >
                <h2>No post yet...</h2>
              </div>
            ) : (
              <div>
                <Row gutter={[16, 16]}>{renderCards}</Row>
              </div>
            )}

            <br />
            <br />
            {postSize >= limit && (
              <div style={{ display: "flex", justifyContent: "center" }}>
                <button onClick={onLoadMore}>Load More</button>
              </div>
            )}
          </div>
        </div>
      </div>

      {showProductsAddPage && (
        <Popup setShowProductsAddPage={setShowProductsAddPage} />
      )}

    </div>
  );
}

export default ShopHome;
