import React, {useLayoutEffect, useState} from "react";
import { Link, useParams } from 'react-router-dom';

import Navigationbar from './Navigationbar';

import Axios from 'axios';

function ItemOverview(props) {
    const {id}=useParams();
    const productid=Number(id);
    const [Products, setProducts]=useState("");

    useLayoutEffect(()=>{
        Axios.get(`http://localhost:5000/getProducts/${productid}`)
      .then((response) =>{
        console.log(response);
        if(response.data.success){

            setProducts(response.data.result[0]);

        } 


        else{
          alert("failed to fetch products")
        }
    })
    },[])



  return (
    <div>
        <Navigationbar/>
        <hr></hr>
        {Products.length === 0 ? (
              <div
                style={{
                  display: "flex",
                  height: "300px",
                  justifyContent: "center",
                  alignItems: "center",
                }}
              >
                <h2>Loading..</h2>
              </div>
            ) : (

              <div style={{height:"500px", width:"500px",borderRadius:"20px",margin:"40px",display:"row"}}>
          <img style={{width:"400px",height:"400px"}}  src={require("../uploads/"+Products.image)} alt="" />
              <div style={{marginLeft:"50px",height:"500px",width:"500px",border:"1px block"}}>
                <Link to="/ShopHomecustomer"><label style={{color:"rgb(235 78 11)", marginLeft:"50px"}}>{Products.shopname}</label></Link></div>
          </div>

            )}




    </div>
  )
}

export default ItemOverview
