import React, {useEffect, useState} from "react";
import { useParams } from 'react-router-dom';

import Navigationbar from './Navigationbar';

import Axios from 'axios';

function ItemOverview(props) {
    const {id}=useParams();
    const productid=Number(id);
    const [Products, setProducts]=useState("");
    console.log(productid)
    useEffect(()=>{
        Axios.get(`http://localhost:5000/getProducts/${productid}`)
      .then((response) =>{
        console.log(response);
        if(response.data.success){

            setProducts(response.data.result[0]);
            console.log("response"+Products.productname);
        } 


        else{
          alert("failed to fetch products")
        }
    })
    },[])
  return (
    <div>
        <Navigationbar/>
        <div style={{height:"500px", width:"500px",borderRadius:"20px",margin:"40px"}}>
        <img style={{height:"400px",width:"400px",borderRadius:"20px"}}
                    src={require("../uploads/" + Products.image)}
                    className="card-img-top-center"
                    alt="..."
                  />   
        </div>
    </div>
  )
}

export default ItemOverview
